 // Cart Open Close
 let cartIcon = document.querySelector("#cart-icon");
 let cart = document.querySelector(".cart");
 let closeCart = document.querySelector("#close-cart");

 // Open Cart
 cartIcon.onclick = () => {
     cart.classList.add("active");
 };

 // Close Cart
 closeCart.onclick = () => {
     cart.classList.remove("active");
 };

 // Add to Cart
 if (document.readyState == "loading") {
     document.addEventListener("DOMContentLoaded", ready);
 } else {
     ready();
 }

 function ready() {
     // Remove product from cart
     var removeCartButtons = document.getElementsByClassName("cart-remove");
     for (var i = 0; i < removeCartButtons.length; i++) {
         var button = removeCartButtons[i];
         button.addEventListener("click", removeCartItem);
     }

     // Quantity changes
     var quantityInputs = document.getElementsByClassName("cart-quantity");
     for (var i = 0; i < quantityInputs.length; i++) {
         var input = quantityInputs[i];
         input.addEventListener("change", quantityChanged);
     }

     // Add product to cart
     var addToCartButtons = document.getElementsByClassName("add-to-cart");
     for (var i = 0; i < addToCartButtons.length; i++) {
         var button = addToCartButtons[i];
         button.addEventListener("click", addToCartClicked);
     }
 }

 function removeCartItem(event) {
     var buttonClicked = event.target;
     buttonClicked.parentElement.parentElement.remove();
     updatetotal();
 }

 function quantityChanged(event) {
     var input = event.target;
     if (isNaN(input.value) || input.value <= 0) {
         input.value = 1;
     }
     updatetotal();
 }

 function addToCartClicked(event) {
     var button = event.target;
     var cartContent = document.getElementsByClassName("cart-content")[0];
     var price = button.getAttribute("data-price");
     var productDiv = document.createElement("div");
     productDiv.classList.add("cart-box");
     productDiv.innerHTML = `
         <div class="cart-price">$${price}</div>
         <input type="number" value="1" class="cart-quantity">
         <button class="cart-remove">Remove</button>
     `;
     cartContent.appendChild(productDiv);

     // Add event listeners to new remove buttons and quantity inputs
     productDiv.querySelector(".cart-remove").addEventListener("click", removeCartItem);
     productDiv.querySelector(".cart-quantity").addEventListener("change", quantityChanged);

     updatetotal();
 }

 function updatetotal() {
     var cartContent = document.getElementsByClassName("cart-content")[0];
     var cartBoxes = cartContent.getElementsByClassName("cart-box");
     var total = 0;
     for (var i = 0; i < cartBoxes.length; i++) {
         var cartBox = cartBoxes[i];
         var priceElement = cartBox.getElementsByClassName("cart-price")[0];
         var quantityElement = cartBox.getElementsByClassName("cart-quantity")[0];
         var price = parseFloat(priceElement.innerText.replace("$", ""));
         var quantity = quantityElement.value;
         total += price * quantity;
     }
     document.getElementsByClassName("total-price")[0].innerText = "$" + total;
 }